jQuery.validator.addMethod('answercheck', function (value, element) {
    return this.optional(element) || /^\bno\b$/.test(value);
  }, "type the correct answer -_-");
 
// validate contact form
$(function() {
  $('#contact').validate({
  rules: {
    name: {
      required: true,
      minlength: 2
    },
    email: {
      required: true,
      email: true
    },
    message: {
      required: true,
      minlength: 10
    },
    answer: {
      required: true,
      answercheck: true
      }
  },
  messages: {
    name: {
      required: "Only robots dont have names. Even most of them do...",
      minlength: "Give me at least 2 characters"
    },
    email: {
      required: "I need a valid e-mail address"
    },
    message: {
      required: "There is no message. I need to know whats on your mind!",
      minlength: "maybe a few more words than that..."
    },
    answer: {
      required: "Wrong answer. You must be a robot..."
    }
  },
  submitHandler: function(form) {
    $(form).ajaxSubmit({
    type:"POST",
    data: $(form).serialize(),
    url:"contact.php",
    success: function() {
      $('#contact :input').attr('disabled', 'disabled');
      $('#contact').fadeTo( "slow", 0.15, function() {
        $(this).find(':input').attr('disabled', 'disabled');
        $(this).find('label').css('cursor','default');
        $('#success').fadeIn();
      });
    },
    error: function() {
      $('#contact').fadeTo( "slow", 0.15, function() {
        $('#error').fadeIn();
        });
    }
  });
  }
});
});